class Intrant extends CI_Controller{
    function __construct()
    {
        parent::__construct();
        $this->load->model('User_model');
        $this->load->model('Educazad_model');
    }

    /*
     * Listing of tb_am_users
     */
    function index()
    {
        $data['all_intrants'] = $this->Educazad_model->get_multiple('tb_im_intrants', array(), 'tb_im_intrants.intrant_id','DESC');

        $data['_view'] = 'intrants/index';
        $this->load->view('layouts/main',$data);
    }

    /*
     * Adding a new tb_am_user
     */
    function add()
    {
        $this->load->library('form_validation');

        $this->form_validation->set_rules('qte_stock','Qte Stock','required');
        $this->form_validation->set_rules('libelle','Libelle','required');
        $this->form_validation->set_rules('categorie_sid','Categorie SID','required');

        if($this->form_validation->run())
        {
            $current_datetime=date('Y-m-d H:i:s');
            $params = array(
                'libelle' => $this->input->post('libelle'),
                'qte_stock' => $this->input->post('qte_stock'),
                'categorie_sid' => $this->input->post('categorie_sid'),
                'date_ajout' => $current_datetime
            );
            $this->Educazad_model->insert_data('tb_im_intrants',$params);
            redirect('intrant/index');
        }
        else
        {
            $data['all_categories'] = $this->Educazad_model->get_multiple('tb_im_categories', array(), 'tb_im_categories.categorie_id','DESC');
            $data['_view'] = 'intrants/add';
            $this->load->view('layouts/main',$data);
        }
    }

    /*
     * Editing a tb_am_user
     */
    function edit($intrantID)
    {
        // check if the tb_am_user exists before trying to edit it
        //$data['candidat'] = $this->User_model->get_tb_am_user($id_asset);
        $data['intrant'] = $this->Educazad_model->get_one_row('tb_im_intrants', array('intrant_id'=>$intrantID));
        $data['all_categories'] = $this->Educazad_model->get_multiple('tb_im_categories', array(), 'tb_im_categories.categorie_id','DESC');

        if(isset($data['intrant']))
        {
            $this->form_validation->set_rules('qte_stock','Qte Stock','required');
            $this->form_validation->set_rules('libelle','Libelle','required');
            $this->form_validation->set_rules('categorie_sid','Categorie SID','required');

            if($this->form_validation->run())
            {
                $current_datetime=date('Y-m-d H:i:s');
                $params = array(
                    'libelle' => $this->input->post('libelle'),
                    'qte_stock' => $this->input->post('qte_stock'),
                    'categorie_sid' => $this->input->post('categorie_sid'),
                    'date_modif' => $current_datetime
                );
                $this->Educazad_model->update_data('tb_im_intrants',$params, array('intrant_id'=>$intrantID));
                redirect('intrant/index');
            }
            else
            {
                $data['_view'] = ('intrants/edit');
                $this->load->view('layouts/main',$data);
            }
        }
        else
            show_error('Table non existant. ');
    }
}